(32, 'cento-de-salgado-refri', 1, 4, 'Cento de Salgado + Refri ', '**Cento de Salgados + Refri** é uma porção generosa de salgados variados acompanhada de um refrigerante gelado, ideal para compartilhar e saborear a qualquer hora.', 'centoSalgadosRefri.png', 29.99, '2020-05-20 15:18:04', '2025-03-30 05:27:10'),
(33, '2-centos-de-salgado-refri', 1, 4, '2 Centos de Salgado + Refri ', '**2 Centos de Salgado + Refri** é uma porção dupla de salgados variados, acompanhada de refrigerante gelado, perfeita para compartilhar e satisfazer a fome de todos.', '2centoSalgadosRefri.png', 45.99, '2020-05-20 15:19:03', '2025-03-30 05:27:24'),
(34, '2-coxinhas-refri', 1, 4, '2 Coxinhas + Refri ', '**2 Coxinhas + Refri** é um combo saboroso com coxinhas crocantes e refrigerante gelado, perfeito para um lanche rápido.', '2CoxinhasMaisRefri.png', 4.50, '2020-05-20 15:20:53', '2025-03-30 05:27:48'),
(35, '4-coxinhas-refri', 1, 4, '4 Coxinhas + Refri ', '**4 Coxinhas + Refri** é um combo irresistível, com quatro coxinhas crocantes e refrigerante gelado, ideal para um lanche saboroso.', '4CoxinhasMaisRefri.png', 6.50, '2020-05-20 15:22:00', '2025-03-30 05:28:00'),
(36, 'pizza-1-refri', 1, 4, 'Pizza + 1 Refri ', '**Pizza + 1 Refri** é a combinação perfeita para uma refeição deliciosa, com uma pizza saborosa e um refrigerante gelado para acompanhar.', 'pizzaRefri.png', 35.99, '2020-05-20 15:22:57', '2025-03-30 05:28:25'),
(37, '2-pizzas-1-refri', 1, 4, '2 Pizzas + 1 Refri ', '**2 Pizzas + 1 Refri** é o combo ideal para compartilhar, com duas pizzas deliciosas e um refrigerante gelado para completar a refeição.', '2pizzaRefri.png', 49.99, '2020-05-20 15:24:17', '2025-03-30 05:28:43'),
(38, 'coca-cola-2-litros', 1, 5, 'Coca cola 2 Litros ', '**Coca-Cola 2 Litros** é a opção clássica e refrescante, perfeita para acompanhar suas refeições ou momentos de descontração.', 'coca2Litros.png', 7.99, '2020-05-20 15:50:14', '2025-03-30 05:28:56'),
(39, 'guarana-antarctica-2-litros', 1, 5, 'Guaraná Antarctica 2 Litros ', '**Guaraná Antarctica 2 litros** é a opção perfeita para quem busca um refrigerante refrescante e com sabor autêntico, ideal para compartilhar em qualquer ocasião.', 'guarana2Litros.png', 7.99, '2020-05-20 15:51:12', '2025-03-30 05:15:59'),
(40, 'fanta-de-2-litros', 1, 5, 'Fanta de 2 Litros ', '**Fanta de 2 litros** oferece um sabor refrescante e frutado, perfeito para compartilhar em qualquer ocasião.', 'fanta2L.png', 7.99, '2020-05-20 15:52:00', '2025-03-30 05:14:24'),
(41, 'refrigerante-de-latinha', 1, 5, 'Refrigerante de Latinha ', '**Refrigerante de latinha** é uma opção prática e refrescante, disponível em diversos sabores para saborear a qualquer hora.', 'refreLata.png', 4.50, '2020-05-20 15:52:53', '2025-03-30 05:14:44'),
(42, 'toddynho', 1, 5, 'Toddynho ', '**Toddynho** é uma bebida achocolatada cremosa e deliciosa, perfeita para um saboroso e prático lanche a qualquer hora.', 'todynho.png', 3.50, '2020-05-20 15:53:49', '2025-03-30 05:15:03'),
(43, 'suco-de-frutas', 1, 5, 'Suco de Frutas ', 'Suco de frutas é uma opção refrescante e saudável, feito com frutas naturais para oferecer sabor e energia a qualquer momento do dia.', 'sucoFruto.png', 4.50, '2020-05-20 15:54:40', '2025-03-30 05:15:26');

