INSERT INTO `product_categories` (`id`, `slug`, `user_id`, `category_name`, `category_description`, `img`, `created_at`, `updated_at`) VALUES
(1, 'hamburgueres', 1, 'Hambúrgueres', 'Hambúrgueres suculentos, feitos com carnes selecionadas e acompanhados de ingredientes frescos. Uma opção deliciosa e prática para qualquer refeição, com diversos sabores para todos os gostos.', 'hamburguers.png', '2020-05-19 14:41:02', '2025-03-30 05:02:12'),
(2, 'pizzas', 1, 'Pizzas', 'Pizzas deliciosas, com diversas opções de sabores, preparadas com ingredientes frescos e de qualidade. Perfeitas para compartilhar ou saborear sozinho a qualquer hora.', 'pizzas.png', '2020-05-19 19:11:32', '2025-03-30 05:01:47'),
(3, 'porcoes', 1, 'Porções', 'Porções de lanches são pequenas quantidades de alimentos rápidas e práticas, perfeitas para matar a fome entre refeições ou compartilhar em eventos. Variam de salgadinhos a mini sanduíches, atendendo diferentes gostos.', 'fritosSalgadosEassados.png', '2020-05-19 21:49:34', '2025-03-30 04:48:08'),
(4, 'combos', 1, 'Combos', 'Combos são combinações de alimentos e bebidas oferecidas com desconto, proporcionando uma refeição completa e prática. Ideais para quem busca economia e variedade.', 'combos.png', '2020-05-20 15:05:44', '2025-03-30 04:59:29'),
(5, 'bebidas', 1, 'Bebidas', 'Bebidas refrescantes, alcoólicas e não alcoólicas, perfeitas para complementar sua refeição ou saciar a sede a qualquer momento. Escolha entre sucos, refrigerantes, cafés e drinks especiais.', 'bebidas.png', '2020-05-20 15:48:25', '2025-03-30 05:01:09');

